function lsmcdWriteToMsgBox(s,t)
{
    if(void 0!==s && 0!==s.length)
    {
        var e={succ:"div#succMsgs",err:"div#errMsgs"}[t];
        s.forEach(
            
        function(s)
        {
            $(e+" ul").append("<li>"+s+"</li>")
        }),
        $("div#display-msgs").css("display","initial")
    }
}

function lsmcdAddrSave()
{
    var s=document.getElementById("addr"),
            t=s.options[s.selectedIndex].value;
    $.getJSON("./lsmcd_usermgr.live.php?do=save_settings&log_file_lvl="+
            t,
    function(s)
    {
        var t=s.succMsgs,e=s.errMsgs;
        if(void 0!==e&&0!==e.length)
        {
            lsmcdWriteToMsgBox(e,"err");
            var l=$("span#errMsgCnt"),i=parseInt(l.text()),n=e.length;
            l.html(i+n),l.closest("button").css("display","initial")
        }
        if(void 0!==t&&0!==t.length)
        {
            lsmcdWriteToMsgBox(t,"succ");
            var a=$("span#succMsgCnt");
            i=parseInt(a.text()),n=t.length;a.html(i+n),
                    a.closest("button").css("display","initial")
        }
    }
            )
}